import React, { useState, useEffect } from 'react';
import axios from 'axios';
import CreateProjectForm from './CreateProjectForm.jsx';

const HomePage = () => {
    const [projects, setProjects] = useState([]);

    useEffect(() => {
        const fetchProjects = async () => {
            try {
                const response = await axios.get('http://localhost:8080/api/projects');
                setProjects(response.data);
            } catch (error) {
                console.error('Error fetching projects:', error);
            }
        };

        fetchProjects();
    }, []);

    const handleCreateProject = async (title) => {
        try {
            const response = await axios.post('http://localhost:8080/api/projects/create', { title });
            console.log('New project created:', response.data);
            // Add logic to update projects state or show a success message
        } catch (error) {
            console.error('Error creating project:', error);
            // Add logic to show an error message
        }
    };

    return (
        <div>
            <h1>Home Page</h1>
            <CreateProjectForm onCreateProject={handleCreateProject} />
            <h2>Existing Projects:</h2>
            <ul>
                {projects.map((project) => (
                    <li key={project.id}>{project.title}</li>
                ))}
            </ul>
        </div>
    );
};

export default HomePage;
