import React, { useState, useEffect } from 'react';
import axios from 'axios';
import CreateProjectForm from './CreateProjectForm.jsx';
import 'bootstrap/dist/css/bootstrap.min.css'; // Import Bootstrap 5 CSS

const HomePage = () => {
    const [projects, setProjects] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchProjects = async () => {
            try {
                const response = await axios.get('http://localhost:8080/api/projects');
                setProjects(response.data);
            } catch (error) {
                setError(error.message || 'Error fetching projects');
            } finally {
                setIsLoading(false);
            }
        };

        fetchProjects();
    }, []);

    const handleCreateProject = async (title) => {
        try {
            const response = await axios.post('http://localhost:8080/api/projects/create', { title });
            console.log('New project created:', response.data);
            setProjects([...projects, response.data]); // Update projects state with new project
        } catch (error) {
            console.error('Error creating project:', error);
            // Add logic to show an error message
        }
    };

    return (
        <div className="container mt-5">
            <h1 className="mb-4">Home Page</h1>
            <CreateProjectForm onCreateProject={handleCreateProject} />
            {isLoading ? (
                <p>Loading projects...</p>
            ) : error ? (
                <p>{error}</p>
            ) : (
                <div>
                    <h2 className="mt-4">Existing Projects:</h2>
                    <ul className="list-group">
                        {projects.map((project) => (
                            <li key={project.id} className="list-group-item">{project.title}</li>
                        ))}
                    </ul>
                </div>
            )}
        </div>
    );
};

export default HomePage;
